        selected.getChildren().add(expandNode);
        selected.setExpanded(true);
        conversationMap.put(expandNode, history);

        // Clear comment
        commentField.clear();

        callOpenAIAsync(history, expandNode);
    }

    private void callOpenAIAsync(List<Map<String, String>> history, TreeItem<String> parentNode) {
        Task<String> task = new Task<>() {
            @Override
            protected String call() throws Exception {
                return callOpenAI(history);
            }
        };
